class Prices extends React.Component {
  state = {
    currency: 'USD',
    filteredValues: [],
    filterOptions: [8000, 9000, 1000],
  };

  onSelect = (e) => {
    const { bpiList } = this.props;
    const value = Number(e.target.value);
    console.log(e.target.value);

    const filtered = bpiList.filter((item) => item >= value);

    console.log(filtered);
    console.log(bpiList);
  };

  render() {
    const { bpiList } = this.props;
    const { filterOptions, filteredValues } = this.props;

    return (
      <div>
        <select
          className="custom-select"
          onChange={this.onSelect}
          style={{ marginBottom: '1rem' }}
        >
          <option value="">Select value</option>
          {
            filterOptions.map(option => (
            <option value="8000">{``}</option>
            ))
          }
          <option value="8000">More then 8000</option>
          <option value="9000">More then 9000</option>
          <option value="10000">More then 10000</option>
        </select>
        <ul className="list-group">
          {bpiList &&
            bpiList.map((bpi) => (
              <li className="list-group-item" key={bpi}>
                {bpi}
              </li>
            ))}
        </ul>
      </div>
    );
  }
}

export default Prices;
