    if (!value) {

    }

    const filtered = bpiList.filter((item) => item >= value);

    console.log(filtered);
    console.log(bpiList);
  };

  render() {
    const { bpiList } = this.props;
    const { filterOptions, filteredValues } = this.state;

    return (
      <div>
        <select
          className="custom-select"
          onChange={this.onSelect}
          style={{ marginBottom: '1rem' }}
        >
          <option value="">Select value</option>
          {filterOptions.map((option) => (
            <option key={option} value={option}>{`Less then ${option}`}</option>
          ))}
        </select>
        <ul className="list-group">
          {bpiList &&
            bpiList.map((bpi) => (
              <li className="list-group-item" key={bpi}>
                {bpi}
              </li>
            ))}
        </ul>
      </div>
    );
  }
}

export default Prices;
